#!/usr/bin/env python3\n\nimport sys\nimport os\nsys.path.append(os.path.join(os.path.dirname(__file__), '..'))\n\nfrom api.router import advisor, statute_resolver\nfrom clean_legal_advisor import LegalQuery\n\ndef test_dowry_harassment_statutes():\n    \"\"\"Test dowry harassment query returns statutes\"\"\"\n    if advisor is None or statute_resolver is None:\n        print(\"SKIP: Advisor not initialized\")\n        return\n    \n    query = LegalQuery(\"dowry harassment\", \"India\", \"criminal\")\n    advice = advisor.provide_legal_advice(query)\n    \n    print(f\"Query: {query.query_text}\")\n    print(f\"Sections found: {len(advice.relevant_sections)}\")\n    print(f\"Jurisdiction: {advice.jurisdiction}\")\n    print(f\"Domain: {advice.domain}\")\n    \n    if advice.relevant_sections:\n        for i, section in enumerate(advice.relevant_sections[:3]):\n            print(f\"  {i+1}. Section {section.section_number}: {section.text[:100]}...\")\n    \n    assert len(advice.relevant_sections) > 0, \"Expected statutes for dowry harassment\"\n    print(\"✓ Dowry harassment test passed\")\n\ndef test_murder_statutes():\n    \"\"\"Test murder query returns statutes\"\"\"\n    if advisor is None or statute_resolver is None:\n        print(\"SKIP: Advisor not initialized\")\n        return\n    \n    query = LegalQuery(\"murder\", \"India\", \"criminal\")\n    advice = advisor.provide_legal_advice(query)\n    \n    print(f\"Query: {query.query_text}\")\n    print(f\"Sections found: {len(advice.relevant_sections)}\")\n    print(f\"Jurisdiction: {advice.jurisdiction}\")\n    print(f\"Domain: {advice.domain}\")\n    \n    if advice.relevant_sections:\n        for i, section in enumerate(advice.relevant_sections[:3]):\n            print(f\"  {i+1}. Section {section.section_number}: {section.text[:100]}...\")\n    \n    assert len(advice.relevant_sections) > 0, \"Expected statutes for murder\"\n    print(\"✓ Murder test passed\")\n\ndef test_statute_dataset_loaded():\n    \"\"\"Test statute dataset is loaded\"\"\"\n    if statute_resolver is None:\n        print(\"SKIP: Statute resolver not initialized\")\n        return\n    \n    print(f\"Statute dataset size: {len(statute_resolver.sections)}\")\n    print(f\"Acts loaded: {len(statute_resolver.acts)}\")\n    print(f\"Jurisdictions: {list(statute_resolver.jurisdiction_sections.keys())}\")\n    \n    assert len(statute_resolver.sections) > 0, \"Statute dataset should be loaded\"\n    print(\"✓ Dataset loaded test passed\")\n\nif __name__ == \"__main__\":\n    print(\"Testing statute retrieval...\")\n    test_statute_dataset_loaded()\n    test_dowry_harassment_statutes()\n    test_murder_statutes()\n    print(\"All statute retrieval tests completed!\")